Action.Str:
var canvas_image = (function(batt, charging, low, r){

var c = document.createElement('canvas');
c.width = r;
c.height = r;

var ctx = c.getContext('2d');
ctx.fillStyle = "white";
if (charging) {
    ctx.strokeStyle = "lightgreen";
} else if (low) {
    ctx.strokeStyle = "tomato";
} else {
    ctx.strokeStyle = "lightsteelblue";
}
ctx.lineWidth = r/8;
ctx.setLineDash([r/16,r/24]);
ctx.beginPath();
ctx.arc(r/2, r/2, r/2-ctx.lineWidth/2, -0.5*Math.PI, batt*2*Math.PI/100-0.5*Math.PI);
ctx.stroke();
ctx.font = "bold " + r*0.6 + "px monospace";
var meas = ctx.measureText(batt);
ctx.fillText(batt < 100 ? batt : "", (r-meas.width)/2, 0.75*r);

return c.toDataURL();
})(global("BATT"), local("bi_status")==2, global("BATT") <= 15, 128);
